-- com.naturaljs.app.sample.mappers.SampleMapper.getSampleList SELECT KEY, INDEX, GUID, ISACTIVE, BALANCE, PICTURE, AGE, EYECOLOR, NAME, GENDER, COMPANY, EMAIL, PHONE, ADDRESS, ABOUT, TO_CHAR(REGISTERED, 'YYYYMMDD') REGISTERED, LATITUDE, LONGITUDE, GREETING, FAVORITEFRUIT FROM SAMPLE
AND name like '%' || #{name} || '%'
AND gender = #{gender}
AND eyeColor = #{eyeColor}
ORDER BY REGISTERED DESC
